select handle_details.taskinst_id, user_.real_name user_, handle_details.claim_time, handle_details.finish_time, handle_details.suggest, handle_details.attitude, handle_details.state from ( select taskinst_id, user_id, claim_time, null finish_time, null suggest, null attitude, handle_state state from bpm_ru_assignee where handle_state=4 union select taskinst_id, user_id, claim_time, null finish_time, null suggest, null attitude, handle_state state from bpm_ru_assignee where handle_state=5 union select taskinst_id, user_id, claim_time, finish_time, suggest, attitude, handle_state state from bpm_hi_assignee where handle_state=6 union select taskinst_id, user_id, null claim_time, null finish_time, null suggest, null attitude, 7 state from bpm_ru_dispatch where is_enabled=1 union select taskinst_id, user_id, null claim_time, dispatch_time finish_time, null suggest, null attitude, 8 state from bpm_hi_dispatch where is_enabled=1 ) handle_details left join smt_imp_user user_ on (user_.id = handle_details.user_id) where
#{task.id}
order by handle_details.finish_time asc
